import React, { useEffect, useState } from "react";


const Tempapp = () => {

  const [city, setCity] = useState(null);

  const [search, setSearch] = useState("pune");


  useEffect(() => {

    const fetchApi = async () => {
      const url = `http://api.openweathermap.org/data/2.5/weather?q=${search}&appid=a71995f9e7435218345d656f0e101cab`
      const response = await fetch(url);

      const resJson = await response.json();
      // console.log(resJson);

      setCity(resJson);
    };
    fetchApi();
  }, [search])


  const changeCity = (event) => {
    setSearch(event.target.value);
  }


  return (
    <>
      <div className="box">
        <div className="inputData">
          <input
            type="search"
            className="inputField"
            onChange={changeCity}

          />
        </div>


        {
          !city
        }



        <div className="info">
          <h2 className="location"> <i className="ri-map-pin-line"></i>{search}
          </h2>

          <h1 className="temp"> {city.main.temp} °Cel</h1>

          <h3 className="tempmin-max">{city.main.temp_min}°Cel | {city.main.temp_max}°Cel</h3>
        </div>

        <div className="wave-one"></div>
        <div className="wave-two"></div>
        <div className="wave-three"></div>

      </div>
    </>
  )
}

export default Tempapp;
